import React, {useRef, useState} from 'react';
import AddItem from './AddItem';

interface Item {
    id: number;
    name: string;
    children?: Item[];
}

const Tree: React.FC = () => {

    const titleNodeRef: React.RefObject<HTMLInputElement> = useRef<HTMLInputElement>(null);
    const newNode: Item = {
        id: getMaxId(nodes) + 1,
        name: titleNodeRef,
    };
    const [nodes, setNodes] = useState<Item[]>([
        {
            id: 1,
            name: 'Element 1',
            children: [
                {
                    id: 2,
                    name: 'Element 1.1',
                    children: [
                        {
                            id: 3,
                            name: 'Element 1.1.1',
                        },
                        {
                            id: 4,
                            name: 'Element 1.1.2',
                        },
                    ],
                },
                {
                    id: 5,
                    name: 'Element 1.2',
                },
            ],

        },
    ]);

    const getMaxId = (arr: Item[]): number => {
        let maxId = 0;
        arr.forEach((item) => {
            if (item.id > maxId) {
                maxId = item.id;
            }
            if (item.children) {
                const childMaxId = getMaxId(item.children);
                if (childMaxId > maxId) {
                    maxId = childMaxId;
                }
            }
        });
        return maxId;
    };

    const onSubmitAdd = (ev: any) => {
        ev.preventDefault();



        const lastId = getMaxId(nodes);

        console.log(lastId); // 5
    }

    const renderTree = (items: Item[]) => {
        return (
            <ul>
                {items.map((item) => (
                    <li key={item.id}>
                        {item.name}
                        {item.children && renderTree(item.children)}
                    </li>
                ))}
            </ul>
        );
    };

    return (
        <div>
            {renderTree(nodes)}
            <form className="form form-control w-100" onSubmit={onSubmitAdd}>
                <div className="form-group m-3">
                    <input ref={titleNodeRef} type="text" className="form-control" placeholder="Тема"></input>
                </div>
                <button className="btn btn-success m-3" type="submit">Добавить</button>
            </form>
            {/*<AddItem />*/}
        </div>
    );
};

export default Tree;
