import React, {useRef, useState} from 'react';
import ModalAdd from "./ModalAdd";

interface Node {
    id: number;
    name: string;
    children?: Node[];
}



const Tree: React.FC = () => {

    const [showModal, setShowModal] = useState(false);

    const handleShowModal = () => {
        setShowModal(true);
    };

    const handleCloseModal = () => {
        setShowModal(false);
    };

    const titleNodeRef: React.RefObject<HTMLInputElement> = useRef<HTMLInputElement>(null);
    const [selectedNode, setSelectedNode] = useState(0);
    const [nodes, setNodes] = useState<Node[]>([
        {
            id: 1,
            name: 'Element 1',
            children: [
                {
                    id: 2,
                    name: 'Element 1.1',
                    children: [
                        {
                            id: 3,
                            name: 'Element 1.1.1',
                        },
                        {
                            id: 4,
                            name: 'Element 1.1.2',
                        },
                    ],
                },
                {
                    id: 5,
                    name: 'Element 1.2',
                },
            ],

        },
    ]);

    const addChildNode = (nodes: Node[], parentId: number, newNode: Node): Node[] => {
        return nodes.map((node) => {
            if (node.id === parentId) {
                return {
                    ...node,
                    children: [...(node.children || []), newNode],
                };
            } else if (node.children) {
                return {
                    ...node,
                    children: addChildNode(node.children, parentId, newNode),
                };
            }
            return node;
        });
    };

    const getMaxId = (arr: Node[]): number => {
        let maxId = 0;
        arr.forEach((node) => {
            if (node.id > maxId) {
                maxId = node.id;
            }
            if (node.children) {
                const childMaxId = getMaxId(node.children);
                if (childMaxId > maxId) {
                    maxId = childMaxId;
                }
            }
        });
        return maxId;
    };

    const onSubmitAdd = (ev: any) => {
        ev.preventDefault();

        const newNode: Node = {
            id: getMaxId(nodes) + 1,
            name: titleNodeRef.current?.value || '',
        };


        if(selectedNode!==0) {
            const updatedNodes = addChildNode(nodes, selectedNode, newNode);
            setNodes(updatedNodes);
        }else{
            setNodes([...nodes, newNode]);
        }
    }

    const renderTree = (nodes: Node[]) => {
        return (
            <ul>
                {nodes.map((node) => (
                    <li key={node.id}>
                        <label>
                            <input
                                type="radio"
                                value={node.id}
                                checked={selectedNode === node.id}
                                onChange={(event) => {
                                    const newId = +event.target.value;
                                    setSelectedNode(newId);
                                    console.log(selectedNode);
                                }}
                            />
                            {node.name}
                        </label>
                        {node.children && renderTree(node.children)}
                    </li>
                ))}
            </ul>
        );
    };

    return (
        <div>
            {renderTree(nodes)}
            <form className="form form-control w-100" onSubmit={onSubmitAdd}>
                <div className="form-group m-3">
                    <input ref={titleNodeRef} type="text" className="form-control" placeholder="Тема"></input>
                </div>
                <button className="btn btn-success m-3" type="submit">Добавить</button>
            </form>
            {/*<AddNode />*/}
            <div className="app">
                <button onClick={handleShowModal}>Show Modal</button>
                <ModalAdd isOpen={showModal} onClose={handleCloseModal} nodes={nodes}>
                    <h1>ModalAdd Content</h1>
                    <p>Some text here...</p>
                    <button onClick={handleCloseModal}>Close</button>
                </ModalAdd>
            </div>
        </div>
    );
};

export default Tree;
