import React, {useRef, useState} from 'react';
import ModalAdd from "./ModalAdd";

interface Node {
    id: number;
    name: string;
    children?: Node[];
}



const Tree: React.FC = () => {

    const [showModalAdd, setShowModalAdd] = useState(false);
    const [showModalEdit, setShowModalEdit] = useState(false);



    const titleNodeRef: React.RefObject<HTMLInputElement> = useRef<HTMLInputElement>(null);
    const titleNodeEditRef: React.RefObject<HTMLInputElement> = useRef<HTMLInputElement>(null);
    const [inputValue, setInputValue] = useState("");
    const [selectedNode, setSelectedNode] = useState(0);
    const [nodes, setNodes] = useState<Node[]>([
        {
            id: 1,
            name: 'Element 1',
            children: [
                {
                    id: 2,
                    name: 'Element 1.1',
                    children: [
                        {
                            id: 3,
                            name: 'Element 1.1.1',
                        },
                        {
                            id: 4,
                            name: 'Element 1.1.2',
                        },
                    ],
                },
                {
                    id: 5,
                    name: 'Element 1.2',
                },
            ],

        },
    ]);

    const addChildNode = (nodes: Node[], parentId: number, newNode: Node): Node[] => {
        return nodes.map((node) => {
            if (node.id === parentId) {
                return {
                    ...node,
                    children: [...(node.children || []), newNode],
                };
            } else if (node.children) {
                return {
                    ...node,
                    children: addChildNode(node.children, parentId, newNode),
                };
            }
            return node;
        });
    };

    const getMaxId = (arr: Node[]): number => {
        let maxId = 0;
        arr.forEach((node) => {
            if (node.id > maxId) {
                maxId = node.id;
            }
            if (node.children) {
                const childMaxId = getMaxId(node.children);
                if (childMaxId > maxId) {
                    maxId = childMaxId;
                }
            }
        });
        return maxId;
    };

    function getNameById(id: number, nodes: Node[]): string | '' {
        for (let i = 0; i < nodes.length; i++) {
            const node = nodes[i];
            if (node.id === id) {
                return node.name;
            } else if (node.children) {
                const result = getNameById(id, node.children);
                if (result !== null) {
                    return result;
                }
            }
        }
        return '';
    }

    const onSubmitAdd = (ev: any) => {
        ev.preventDefault();

        const newNode: Node = {
            id: getMaxId(nodes) + 1,
            name: titleNodeRef.current?.value || '',
        };

        if(selectedNode!==0) {
            const updatedNodes = addChildNode(nodes, selectedNode, newNode);
            setNodes(updatedNodes);
        }else{
            setNodes([...nodes, newNode]);
        }
        setShowModalAdd(false);
    }

    const updateName = (nodes: Node[], id: number, newName: string) => {
        if (nodes.id === id) {
            // Если id текущего элемента совпадает с искомым, изменяем его name
            nodes.name = newName;
        } else if (nodes.children) {
            // Если у текущего элемента есть дочерние элементы, рекурсивно вызываем эту же функцию для каждого дочернего элемента
            for (const child of nodes.children) {
                updateName(child, id, newName);
            }
        }
    };

    const renderTree = (nodes: Node[]) => {
        return (
            <ul>

                {nodes.map((node) => (
                    <li key={node.id}>
                        <label>
                            <input
                                type="radio"
                                value={node.id}
                                checked={selectedNode === node.id}
                                onChange={(event) => {
                                    const newId = +event.target.value;
                                    // console.log(newId);
                                    if (newId === selectedNode) {
                                        setSelectedNode(0);
                                    } else {
                                        setSelectedNode(newId);
                                    }
                                }}
                            />
                            {node.name}
                        </label>
                        {node.children && renderTree(node.children)}
                    </li>
                ))}
            </ul>
        );
    };

    const handleShowModalAdd = () => {
        setShowModalAdd(true);
    };

    const handleCloseModalAdd = () => {
        setShowModalAdd(false);
    };
    const handleShowModalEdit = () => {
        setShowModalEdit(true);
        setInputValue(getNameById(selectedNode, nodes) ?? "");
    };

    const handleCloseModalEdit = () => {
        setShowModalEdit(false);
    };

    return (
        <div>
            <ul>
            <li>
                <label>
                    <input
                        type="radio"
                        value='0'
                        checked={selectedNode === 0}
                        onChange={(event) => {
                            const newId = +event.target.value;
                            setSelectedNode(newId);
                        }}
                    />
                    Добавить Корневой элемент
                </label>
            </li>
            </ul>
            {renderTree(nodes)}

            {/*<AddNode />*/}
            <div className="app">
                <button onClick={handleShowModalAdd}>Show Modal</button>
                <ModalAdd isOpen={showModalAdd} onClose={handleCloseModalAdd} nodes={nodes}>
                    <form className="form form-control w-100" onSubmit={onSubmitAdd}>
                        <div className="form-group m-3">
                            <input ref={titleNodeRef} type="text" className="form-control" placeholder="Тема"></input>
                        </div>
                        <button className="btn btn-success m-3" type="submit">Добавить</button>
                    </form>
                    <button onClick={handleCloseModalAdd}>Close</button>
                </ModalAdd>
                <button onClick={handleShowModalEdit} disabled={selectedNode===0}>Show Modal</button>
                <ModalAdd isOpen={showModalEdit} onClose={handleCloseModalEdit} nodes={nodes}>
                    <form className="form form-control w-100" onSubmit={onSubmitAdd}>
                        <div className="form-group m-3">
                            <input ref={titleNodeEditRef} defaultValue={inputValue} type="text" className="form-control" placeholder="Тема"></input>
                        </div>
                        <button className="btn btn-success m-3" type="submit">Добавить</button>
                    </form>
                    <button onClick={handleCloseModalEdit}>Close</button>
                </ModalAdd>
            </div>
        </div>
    );
};

export default Tree;
