import React, {useRef, useState} from "react";

interface Node {
    id: number;
    name: string;
    children?: Node[];
}

interface ModalAddProps {
    isOpen: boolean;
    onClose: () => void;
    nodes: Node[];
    setNodes: ()=>void;
    selectedNode: number;
    children: React.ReactNode;
}

function ModalAdd({ isOpen, onClose, nodes, selectedNode, children, setNodes }: ModalAddProps) {
    const titleNodeRef: React.RefObject<HTMLInputElement> = useRef<HTMLInputElement>(null);

    if (!isOpen) {return null;}

    const addChildNode = (nodes: Node[], parentId: number, newNode: Node): Node[] => {
        return nodes.map((node) => {
            if (node.id === parentId) {
                return {
                    ...node,
                    children: [...(node.children || []), newNode],
                };
            } else if (node.children) {
                return {
                    ...node,
                    children: addChildNode(node.children, parentId, newNode),
                };
            }
            return node;
        });
    };
    const getMaxId = (arr: Node[]): number => {
        let maxId = 0;
        arr.forEach((node) => {
            if (node.id > maxId) {
                maxId = node.id;
            }
            if (node.children) {
                const childMaxId = getMaxId(node.children);
                if (childMaxId > maxId) {
                    maxId = childMaxId;
                }
            }
        });
        return maxId;
    };
    const onSubmitAdd = (ev: any) => {
        ev.preventDefault();

        const newNode: Node = {
            id: getMaxId(nodes) + 1,
            name: titleNodeRef.current?.value || '',
        };


        if(selectedNode!==0) {
            const updatedNodes = addChildNode(nodes, selectedNode, newNode);
            setNodes(updatedNodes);
        }else{
            setNodes([...nodes, newNode]);
        }
    }

    return (
        <div className="modal">
            <div className="modal-overlay" onClick={onClose} />
            <div className="modal-content">{children}  aaaaa
                <form className="form form-control w-100" onSubmit={onSubmitAdd}>
                    <div className="form-group m-3">
                        <input ref={titleNodeRef} type="text" className="form-control" placeholder="Тема"></input>
                    </div>
                    <button className="btn btn-success m-3" type="submit">Добавить</button>
                </form>
            </div>
        </div>
    );
}

export default ModalAdd;
