import React, { useState } from 'react';
import AddItem from './AddItem';

interface Item {
    id: number;
    name: string;
    children?: Item[];
}

const Tree: React.FC = () => {
    const [nodes, setNodes] = useState<Item[]>([
        {
            id: 1,
            name: 'Element 1',
            children: [
                {
                    id: 2,
                    name: 'Element 1.1',
                    children: [
                        {
                            id: 3,
                            name: 'Element 1.1.1',
                        },
                        {
                            id: 4,
                            name: 'Element 1.1.2',
                        },
                    ],
                },
                {
                    id: 5,
                    name: 'Element 1.2',
                },
            ],
        },
    ]);

    const renderTree = (items: Item[]) => {
        return (
            <ul>
                {items.map((item) => (
                    <li key={item.id}>
                        {item.name}
                        {item.children && renderTree(item.children)}
                    </li>
                ))}
            </ul>
        );
    };

    return (
        <div>
            {renderTree(nodes)}
            {/*<AddItem />*/}
        </div>
    );
};

export default Tree;
