import React, {useRef, useState} from 'react';
import AddItem from './AddItem';

interface Item {
    id: number;
    name: string;
    children?: Item[];
}

const Tree: React.FC = () => {

    const titleNodeRef: React.RefObject<HTMLInputElement> = useRef<HTMLInputElement>(null);

    const [nodes, setNodes] = useState<Item[]>([
        {
            id: 1,
            name: 'Element 1',
            children: [
                {
                    id: 2,
                    name: 'Element 1.1',
                    children: [
                        {
                            id: 3,
                            name: 'Element 1.1.1',
                        },
                        {
                            id: 4,
                            name: 'Element 1.1.2',
                        },
                    ],
                },
                {
                    id: 5,
                    name: 'Element 1.2',
                },
            ],

        },
    ]);

    const addChildNode = (nodes: Item[], parentId: number, newNode: Item): Item[] => {
        return nodes.map((node) => {
            if (node.id === parentId) {
                return {
                    ...node,
                    children: [...(node.children || []), newNode],
                };
            } else if (node.children) {
                return {
                    ...node,
                    children: addChildNode(node.children, parentId, newNode),
                };
            }
            return node;
        });
    };

    const getMaxId = (arr: Item[]): number => {
        let maxId = 0;
        arr.forEach((item) => {
            if (item.id > maxId) {
                maxId = item.id;
            }
            if (item.children) {
                const childMaxId = getMaxId(item.children);
                if (childMaxId > maxId) {
                    maxId = childMaxId;
                }
            }
        });
        return maxId;
    };

    const onSubmitAdd = (ev: any) => {
        ev.preventDefault();

        const newNode: Item = {
            id: getMaxId(nodes) + 1,
            name: titleNodeRef.current?.value || '',
        };

        setNodes([...nodes, newNode]);

        const parentId = 0; // id узла, в который нужно добавить новый узел
        const updatedNodes = addChildNode(nodes, parentId, newNode);
        setNodes(updatedNodes);
    }

    const renderTree = (items: Item[]) => {
        return (
            <ul>
                {items.map((item) => (
                    <li key={item.id}>
                        <label>
                            <input
                                type="radio"
                                value={item.name}
                                checked={selectedItem === item.id}
                                onChange={(event) => {
                                    const newName = event.target.value;
                                    setSelectedItem(newName);
                                    const updatedItems = items.map((i) =>
                                        i.id === item.id ? { ...i, name: newName } : i
                                    );
                                    setItems(updatedItems);
                                }}
                            />
                            {item.name}
                        </label>
                        {item.children && renderTree(item.children)}
                    </li>
                ))}
            </ul>
        );
    };

    return (
        <div>
            {renderTree(nodes)}
            <form className="form form-control w-100" onSubmit={onSubmitAdd}>
                <div className="form-group m-3">
                    <input ref={titleNodeRef} type="text" className="form-control" placeholder="Тема"></input>
                </div>
                <button className="btn btn-success m-3" type="submit">Добавить</button>
            </form>
            {/*<AddItem />*/}
        </div>
    );
};

export default Tree;
