import React, {useRef, useState} from 'react';
import AddItem from './AddItem';

interface Item {
    id: number;
    name: string;
    children?: Item[];
}

const Tree: React.FC = () => {

    const titleNodeRef: React.RefObject<HTMLInputElement> = useRef<HTMLInputElement>(null);

    const [nodes, setNodes] = useState<Item[]>([
        {
            id: 9,
            name: 'Element 1',
            children: [
                {
                    id: 2,
                    name: 'Element 1.1',
                    children: [
                        {
                            id: 3,
                            name: 'Element 1.1.1',
                        },
                        {
                            id: 4,
                            name: 'Element 1.1.2',
                        },
                    ],
                },
                {
                    id: 5,
                    name: 'Element 1.2',
                },
            ],
        },
    ]);

    const onSubmitAdd = (ev: any) => {
        ev.preventDefault();
        const newNode: Item = {
            id: 6,
            name: 'Element 1.3',
        };
        const lastId = Math.max(...nodes.map((node) => node.id));
        console.log(lastId);
    }

    const renderTree = (items: Item[]) => {
        return (
            <ul>
                {items.map((item) => (
                    <li key={item.id}>
                        {item.name}
                        {item.children && renderTree(item.children)}
                    </li>
                ))}
            </ul>
        );
    };

    return (
        <div>
            {renderTree(nodes)}
            <form className="form form-control w-100" onSubmit={onSubmitAdd}>
                <div className="form-group m-3">
                    <input ref={titleNodeRef} type="text" className="form-control" placeholder="Тема"></input>
                </div>
                <button className="btn btn-success m-3" type="submit">Добавить</button>
            </form>
            {/*<AddItem />*/}
        </div>
    );
};

export default Tree;
